import { InsertEmoticon } from "@material-ui/icons";
import React, { useState } from "react";
import Form from "./form";
import List from "./list";

const App = () => {
  let [list, setList] = useState({
    item: '',
  })
  //  change item 
  let eventChange = (e) => {
      const{name,value} = e.target;
      setList((preVal)=>{
          return{...preVal,
          }
      })
  }
  //sub item
  let subEvent = (e) => {
    e.preventDefault();
  }
  // delete item
  let delItem = () => {
    console.log("deleted")
  }
  return (
    <>
      <div className="row justify-content-center">
        <div className="col-9 col-md-4">
          <div className="card">
            <h1 className="text-center">ToDo App</h1>
            <div className="card-body">

              <Form
                submit={subEvent}
                change={eventChange}
                name="item"
                value={list.item}
              />
              <ul>
                <List
                  click={delItem}
                  listitem={list.item}
                />
              </ul>
            </div>
          </div>
        </div>
      </div>
    </>
  )
}
export default App;
